import { fetchUtils } from "react-admin";
import jsonServerProvider from 'ra-data-json-server';

const servicesHost = 'http://localhost:8082';

const httpClient = (url, options = {}) => {
    if (!options.headers) {
        options.headers = new Headers({ Accept: 'application/json' });
    }
    options.headers.set('Access-Control-Expose-Headers','X-Total-Count');
    return fetchUtils.fetchJson(url, options);
};

const dataProvider = jsonServerProvider(servicesHost, httpClient);

const dataProv = {
    ...dataProvider,
    create: (resource, params) => {
        console.log(" ==== DATA PROVIDER RUNN ==== ");
        
        if (resource !== 'member' || !params.data.memberFace) {
            return dataProvider.create(resource, params);
        }
        
        const formData = new FormData();
        console.log(" ==== DATA PROVIDER RUNN ==== ");
        console.log(params.data.memberFace);
        const newPictures = params.data.memberFace.filter(
            p => p.rawFile instanceof File
        );
        const formerPictures = params.data.memberFace.filter(
            p => !(p.rawFile instanceof File)
        );

        return Promise.all(newPictures.map(convertFileToBase64))
            .then(base64Pictures =>
                base64Pictures.map(picture64 => ({
                    src: picture64,
                    title: `${params.data.title}`,
                }))
            )
            .then(transformedNewPictures =>
                dataProvider.create(resource, {
                    ...params,
                    data: {
                        ...params.data,
                        pictures: [
                            ...transformedNewPictures,
                            ...formerPictures,
                        ],
                    },
                })
            );
    },
};

const convertFileToBase64 = file =>
    new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = () => resolve(reader.result);
        reader.onerror = reject;

        reader.readAsDataURL(file.rawFile);
    });
export default dataProv;
