import * as React from "react";
import {Create, EditButton, Filter, TextInput, ReferenceInput, SelectInput,List, Datagrid, TextField, SimpleForm,Edit, DeleteButton } from 'react-admin';
// ReferenceField의 source는 forignkey, reference는 참조할 테이블 명시하는 것


export const StateList = props => (
    <List {...props}>
        <Datagrid rowClick="edit">
            <TextField source="id" />
            <TextField source="memberId" />
            <TextField source="stateNote" />
            <TextField source="stateTime" />
            <EditButton/>
        </Datagrid>
    </List>
);
export const StateFilter = (props) => (
    <Filter {...props}>
        <TextInput label="Search" source="q" alwaysOn />
        <ReferenceInput label="User" source="userId" reference="users" allowEmpty>
            <SelectInput optionText="name" />
        </ReferenceInput>
    </Filter>
);
export const StateEdit = props => (
    <Edit {...props} undoable = {false} >
        <SimpleForm>
            <TextInput disabled source="id" />
            <TextInput source="stateNote" />
        </SimpleForm>
    </Edit>
);
export const StateCreate = props => (
    <Create {...props}>
        <SimpleForm>
            <TextInput source="stateNote" />
            <TextInput source="memberId" />
        </SimpleForm>
    </Create>
);
